import 'package:shared_preferences/shared_preferences.dart';const String _SearchHistoryDataKey = 'SearchHistoryDataKey';//获取搜索记录并返回Future<List<String>> getHistoryData() async {  SharedPreferences prefer;  try{    prefer = await SharedPreferences.getInstance();  } catch(e) {    return [];  }  List<String> _searchHistory = prefer.getStringList(_SearchHistoryDataKey);  if (_searchHistory == null) return [];  return _searchHistory;}//添加搜索记录并返回Future<List<String>> addHistoryData(String searchKey) async {  List<String> _searchHistory = await getHistoryData();  if (searchKey == null || searchKey.length == 0) {    return _searchHistory;  }  if (_searchHistory.contains(searchKey)) {    _searchHistory.remove(searchKey);  }  _searchHistory.insert(0, searchKey);  SharedPreferences prefer = await SharedPreferences.getInstance();  prefer.setStringList(_SearchHistoryDataKey, _searchHistory);  return _searchHistory;}//删除搜索记录并返回Future<List<String>> deleteHistoryData() async {  SharedPreferences prefer = await SharedPreferences.getInstance();  prefer.setStringList(_SearchHistoryDataKey, []);  return [];}