import 'package:flutter/material.dart';import 'package:ecook/collections/recollections.dart';import 'package:ecook/home/rehome.dart';import 'package:ecook/news/renews.dart';class ScaffoldRoute extends StatefulWidget {  @override  _ScaffoldRouteState createState() => _ScaffoldRouteState();}class _ScaffoldRouteState extends State<ScaffoldRoute> {  int _selectedIndex = 0;  List<Widget> pages = new List();  @override  void initState() {    pages      ..add(homeVC())      ..add(newsVC())      ..add(collectionVC());  }  @override  Widget build(BuildContext context) {    return Scaffold(      bottomNavigationBar: BottomNavigationBar( // 底部导航        items: <BottomNavigationBarItem>[          BottomNavigationBarItem(              icon: Image(                  width: 22,                  height: 22,                  image: AssetImage("lib/img/tab_home_def@2x.png")),              activeIcon: Image(                  width: 22,                  height: 22,                  image: AssetImage("lib/img/tab_home@2x.png")),              title: Text('首页')),          BottomNavigationBarItem(              icon: Image(                  width: 22,                  height: 22,                  image: AssetImage("lib/img/tab_zx_def@2x.png")),              activeIcon: Image(                  width: 22,                  height: 22,                  image: AssetImage("lib/img/tab_zx@2x.png")),              title: Text('资讯')),          BottomNavigationBarItem(              icon: Image(                  width: 22,                  height: 22,                  image: AssetImage("lib/img/tab_sc_def@2x.png")),              activeIcon: Image(                  width: 22,                  height: 22,                  image: AssetImage("lib/img/tab_sc@2x.png")),              title: Text('收藏')),        ],        currentIndex: _selectedIndex,        fixedColor: Colors.green,        onTap: _onItemTapped,        selectedFontSize: 10,        unselectedFontSize: 10,      ),      body: pages[_selectedIndex],    );  }  void _onItemTapped(int index) {    setState(() {      _selectedIndex = index;    });  }}