import 'package:ecook/modules/collections/recollections.dart';import 'package:ecook/modules/home/rehome.dart';import 'package:ecook/modules/news/renews.dart';import 'package:flutter/material.dart';class ScaffoldRoute extends StatefulWidget {  @override  _ScaffoldRouteState createState() => _ScaffoldRouteState();}class _ScaffoldRouteState extends State<ScaffoldRoute> {  int _selectedIndex = 0;  List<Widget> pages = new List();  @override  // ignore: must_call_super  void initState() {    pages      ..add(HomeVC())      ..add(NewsVC())      ..add(CollectionVC());  }  @override  Widget build(BuildContext context) {    return Scaffold(      bottomNavigationBar: BottomNavigationBar( // 底部导航        items: <BottomNavigationBarItem>[          BottomNavigationBarItem(              icon: Image(                  width: 22,                  height: 22,                  image: AssetImage("assets/tab_home_def.png")),              activeIcon: Image(                  width: 22,                  height: 22,                  image: AssetImage("assets/tab_home.png")),              title: Text('首页')),          BottomNavigationBarItem(              icon: Image(                  width: 22,                  height: 22,                  image: AssetImage("assets/tab_zx_def.png")),              activeIcon: Image(                  width: 22,                  height: 22,                  image: AssetImage("assets/tab_zx.png")),              title: Text('资讯')),          BottomNavigationBarItem(              icon: Image(                  width: 22,                  height: 22,                  image: AssetImage("assets/tab_sc_def.png")),              activeIcon: Image(                  width: 22,                  height: 22,                  image: AssetImage("assets/tab_sc.png")),              title: Text('收藏')),        ],        currentIndex: _selectedIndex,        fixedColor: Colors.green,        onTap: _onItemTapped,        selectedFontSize: 10,        unselectedFontSize: 10,      ),      body: pages[_selectedIndex],    );  }  void _onItemTapped(int index) {    setState(() {      _selectedIndex = index;    });  }}