import 'package:flutter/material.dart';import 'dart:math';class ColorHelper {  //随机色  static Color slRandomColor({int r = 255, int g = 255, int b = 255, a = 255}) {    if (r == 0 || g == 0 || b == 0) return Colors.black;    if (a == 0) return Colors.white;    return Color.fromARGB(      a,      r != 255 ? r : Random.secure().nextInt(r),      g != 255 ? g : Random.secure().nextInt(g),      b != 255 ? b : Random.secure().nextInt(b),    );  }  //16进制字符串转颜色,#FFFFFF  static Color hexStringToColor(String hexString,{double alpha = 1}) {    if (hexString == null || hexString.length != 7 ||        int.tryParse(hexString.substring(1, 7), radix: 16) == null) {      return Colors.black;    }    return hexToColor(int.parse(hexString.substring(1, 7), radix: 16) + 0x000000,        alpha: alpha);  }  //16进制转颜色,0xFFFFFF  static Color hexToColor(int hex,{double alpha = 1}){    if (alpha < 0) {      alpha = 0;    } else if (alpha > 1) {      alpha = 1;    }    return Color.fromRGBO((hex & 0xFF0000) >> 16 ,        (hex & 0x00FF00) >> 8,        (hex & 0x0000FF) >> 0,        alpha);  }}