import 'dart:ui';import 'package:flutter/material.dart';class REScreenInfo {  /// 屏幕尺寸  static final Size screenSize = MediaQueryData.fromWindow(window).size;  /// 屏幕宽  static final double screenWidth = MediaQueryData.fromWindow(window).size.width;  /// 屏幕高  static final double screenHeight = MediaQueryData.fromWindow(window).size.height;  /// 状态栏高度 （iOS 全面屏为 44，其它为 20）  static final double statusBarHeight = MediaQueryData.fromWindow(window).padding.top;  /// 头部导航栏高度  static final double topNavHeight = 44;  /// 状态栏高度+头部导航栏高度  static final double topBarHeight = statusBarHeight + topNavHeight;  /// 底部高度 （iOS 全面屏为 34，其它为 0）  static final double bottomHeight = MediaQueryData.fromWindow(window).padding.bottom;  /// 底部导航栏高度  static final double bottomNavHeight = 49 + bottomHeight;  /// 屏幕宽/375  static final double screenWidthRatio = screenWidth/375;  /// 屏幕高/667  static final double screenHeightRatio = screenHeight/667;}