class HomeBean {
  String name;
  String state;
  List<ListBean> list;
  String message;

  static HomeBean fromMap(Map<String, dynamic> map) {
    if (map == null) return null;
    HomeBean homeBeanBean = HomeBean();
    homeBeanBean.name = map['name'];
    homeBeanBean.state = map['state'];
    homeBeanBean.list = List()
      ..addAll((map['list'] as List ?? []).map((o) => ListBean.fromMap(o)));
    homeBeanBean.message = map['message'];
    return homeBeanBean;
  }

  Map toJson() => {
    "name": name,
    "state": state,
    "list": list,
    "message": message,
  };
}

